import React from 'react';
import styled from 'styled-components';
import {connect} from 'react-redux';
import {getGlobalStories} from '../actions';

class StoryView extends React.Component {
  constructor(props){
    super(props);
  }



  render(){
    return (
      <div>
        <h1>{this.props.globalStories[0].title}</h1>
        <h2>{this.props.globalStories[0].country}</h2>
        <p>{this.props.globalStories[0].description}</p>
        <button>Donate</button>
      </div>
    );
  }
}

const mstp = (state) => {
  return {
    globalStories: state.globalStories
  };
}
export default connect(mstp, null)(StoryView);
