import React from 'react';
import styled from 'styled-components';
import {connect} from 'react-redux';
import {getGlobalStories} from '../actions'
import Popup from 'reactjs-popup';

import ListviewComponent from '../components/ListviewComponent';

const ListContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

class CoordinatorHome extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      open: false
    }
  }

  openModal = () => {
    this.setState({ open: true })
  }

  closeModal = () => {
    this.setState({ open: false })
  }

  componentDidMount(){
    this.props.getGlobalStories();
  }

  storyView = e => {
    return (
      <Popup open={this.state.open} closeOnDocumentClick onClose={this.closeModal}>
        <div>
          <a onClick={this.closeModal}>
            &times;
          </a>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Beatae magni
          omnis delectus nemo, maxime molestiae dolorem numquam mollitia, voluptate
          ea, accusamus excepturi deleniti ratione sapiente! Laudantium, aperiam
          doloribus. Odit, aut.
        </div>
      </Popup>
    );
  }

  render(){
    return (
      <div>
        <ListContainer>
          {this.props.globalStories.map(story => <ListviewComponent onClick={this.storyView} story={story}/>)}
        </ListContainer>

      </div>
    );
  }
}

const mstp = (state) => {
  return {
    globalStories: state.globalStories
  };
}

export default connect(mstp, {getGlobalStories: getGlobalStories})(CoordinatorHome)
