import React from 'react';
import styled from 'styled-components';
import {connect} from 'react-redux';
import {getGlobalStories} from '../actions';

class StoryView extends React.Component {
  constructor(props){
    super(props);
  }

story = () => this.props.globalStories.filter(item => item.id === this.props.match.params);

  render(){
    return (
      <div>
        <h1>{this.story[0].title}</h1>
        <h2>{this.story.country}</h2>
        <p>{this.story.description}</p>
        <button>Donate</button>
      </div>
    );
  }
}

const mstp = (state) => {
  return {
    globalStories: state.globalStories
  };
}
export default connect(mstp, null)(StoryView);
