import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';

class StoryView extends React.Component {
  constructor(props){
    super(props);
  }

  render(){
    return (
      <div>
        {story = this.props.globalStories.filter(story => story.id === this.props.match.params)}
        <h1>{story.title}</h1>
        <h2>{story.country}</h2>
        <p>{story.description}</p>
        <button>Edit</button>
        <button>Delete</button>
      </div>
    );
  }
}

const mstp = (state) => {
  return {
    globalStories: state.globalStories
  };
}
export default connect(mstp , null)(StoryView);
